import React from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { Image } from 'react-native';
const durumlar = [
    {
        id: '1', name: 'John Doe', durumlar: [
            { durum: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg', time: '14:45' },
            { durum: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg', time: '14:45' }

        ], time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg'
    },
    {
        id: '2', name: 'John Doe 2', durumlar: [
            { durum: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg', time: '14:45' },
            { durum: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg', time: '14:45' }

        ], time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg'
    },
];



const ChatItem = ({ item, onItemLongPress, onItemPress, selectedItems }) => (
    <TouchableOpacity style={[styles.listItem, { backgroundColor: selectedItems.includes(item.id) ? '#ddd' : 'transparent',
    borderRadius: "100%",
    border:item.durumlar.length!==0? '3px solid #47b900' : '0px solid #47b900'
 }]} onLongPress={() => onItemLongPress(item.id)}
        onPress={() => onItemPress(item.id)} >
        <Image source={{ uri: item.avatar }} style={styles.avatar} />
        <View style={styles.chatInfo}>
            <View style={styles.chatHeader}>
                <Text style={styles.listItemText}>{item.name}</Text>
                
            </View>
            <Text style={styles.listItemMessage}>{item.time}</Text>
        </View>
    </TouchableOpacity>
);

const StatusScreen = ({ onItemLongPress, onItemPress, isMultiSelectMode, selectedItems }) => {
    console.log(selectedItems);
    return (
        <FlatList
            data={durumlar}
            renderItem={({ item }) => (
                <ChatItem
                    item={item}
                    onItemLongPress={() => onItemLongPress(item.id)}
                    onItemPress={() => onItemPress(item.id)}
                    selectedItems={selectedItems}
                />
            )}
            keyExtractor={item => item.id}
            style={styles.container}
        />
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5'
    },

    listItem: {
        flexDirection: 'row',
        padding: 15,
        borderBottomWidth: 1,
        borderBottomColor: '#e0e0e0'
    },
    listItemText: {
        fontSize: 18,
        fontWeight: 'bold'
    },
    listItemMessage: {
        fontSize: 14,
        color: '#888'
    },
    avatar: {
        width: 50,
        height: 50,
        borderRadius: 25,
        marginRight: 10
    },
    chatInfo: {
        flex: 1
    },
    chatHeader: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    time: {
        fontSize: 12,
        color: '#888'
    }
});


export default StatusScreen;
