    };
  
     const handleSubmit = () => {
    if (inputValue.trim() === '') {
      // Handle validation or show an alert
    } else {
      if (selectedItem) {
        // Update existing item
        setItems((prevItems) =>
          prevItems.map((item) =>
            item.id === selectedItem.id ? { ...item, value: inputValue } : item
