import React, { useState } from 'react';
import { View, Text, Image, ScrollView, TouchableOpacity, Modal, TextInput, Button } from 'react-native';
import { FontAwesome } from '@expo/vector-icons'; // FontAwesome ikonları için

const DailyScreen = ({ user, onItemLongPress }) => {
  const [showModal, setShowModal] = useState(false);
  const [dailyLogs, setDailyLogs] = useState([
    { date: '2024-02-01', log: "Bugün harika bir gün geçirdim! Birkaç arkadaşımla buluştum ve güzel vakit geçirdik." },
    { date: '2024-02-02', log: "Ders çalıştım ve birkaç kitap okudum. Akşam yemeği için favori restoranıma gittim." },
    { date: '2024-02-03', log: "Yürüyüş yaptım ve doğayla vakit geçirdim. Akşam film izledim ve dinlendim." }
  ]);
  const [newLog, setNewLog] = useState('');

  const addLog = () => {
    if (newLog.trim() !== '') {
      setDailyLogs([...dailyLogs, { date: new Date().toISOString().slice(0, 10), log: newLog }]);
      setNewLog('');
      setShowModal(false);
    }
  };

  return (
    <ScrollView style={{ flex: 1 }}>
      <View style={{ alignItems: 'center' }}>
        <Image
          source={{ uri: user.backgroundImage }}
          style={{ width: '100%', height: 200 }}
        />
        <Image
          source={{ uri: user.avatar }}
          style={{ width: 150, height: 150, borderRadius: 75, borderWidth: 5, borderColor: '#ebebeb', marginTop: -75 }}
        />
      </View>

      <View style={{ marginHorizontal: 20 }}>
        <TouchableOpacity onPress={() => setShowModal(true)} style={{ alignItems: 'center', backgroundColor: '#1877f2', padding: 10, borderRadius: 5, marginBottom: 20 }}>
          <Text style={{ color: 'white' }}>Günlük Ekle</Text>
        </TouchableOpacity>

        {dailyLogs.map((log, index) => (
          <TouchableOpacity key={index} onPress={() => onItemLongPress(log.log)} style={{ marginBottom: 20 }}>
            <Text style={{ fontWeight: 'bold', marginBottom: 5 }}>{log.date}</Text>
            <Text numberOfLines={3} ellipsizeMode="tail">{log.log}</Text>
            <FontAwesome name="angle-right" size={24} color="black" style={{ position: 'absolute', top: 10, right: 10 }} />
          </TouchableOpacity>
        ))}

        <Modal visible={showModal} animationType="slide">
          <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <TextInput
              placeholder="Günlük yazınız..."
              multiline
              value={newLog}
              onChangeText={setNewLog}
              style={{ borderWidth: 1, borderColor: '#ccc', padding: 10, width: '80%', marginBottom: 10 }}
            />
            <Button title="Ekle" onPress={addLog} />
            <Button title="İptal" onPress={() => setShowModal(false)} color="red" />
          </View>
        </Modal>
      </View>
    </ScrollView>
  );
};

export default DailyScreen;
