import React from 'react';
import { Ionicons } from '@expo/vector-icons';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { Image } from 'react-native';
const chats = [
    { id: '1', en: 'about', means: [
        {mean:'hemen hemen',type:'zf.'},
        {mean:'aşağı yukarı',type:'zf.'},
        {mean:'yaklaşık',type:'zf.'},
    ], 
    examples:[{name:'it is about'},{name:'she is about'}]
    , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '2', en: 'above', means: [
        {mean:'üzerine',type:'zf.'},
        {mean:'yukarısında',type:'zf.'},
        {mean:'yukarıda',type:'zf.'},
    ], 
    examples:[{name:'it is above'},{name:'she is above'}]
    , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },

    { id: '1', en: 'action', means: [
        {mean:'çalışma',type:'i.'},
        {mean:'davranış',type:'i.'},
        {mean:'aksiyon',type:'i.'},
    ], 
    examples:[{name:'it is action'},{name:'she is action'}]
    , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '2', en: 'above', means: [
        {mean:'üzerine',type:'zf.'},
        {mean:'yukarısında',type:'zf.'},
        {mean:'yukarıda',type:'zf.'},
    ], 
    examples:[{name:'it is above'},{name:'she is above'}]
    , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },


     // ... Diğer sohbetler
];

const ChatItem = ({ item, onItemLongPress, onItemPress, selectedItems }) => (
    <TouchableOpacity
      style={[
        styles.listItem,
        {
          marginVertical: 5,
          marginHorizontal: 10,
          backgroundColor: selectedItems.includes(item.id) ? '#ddd' : 'transparent',
        },
      ]}
      onLongPress={() => onItemLongPress(item.id)}
      onPress={() => onItemPress(item.id)}
    >
        <Image source={{ uri: item.image }} style={styles.avatar} />
      <View style={styles.listItemContent}>
        
        <View style={styles.chatInfo}>
          {/* İngilizce kelime kartı */}
          <View style={styles.wordCard}>
            <View style={styles.wordDetails}>
              <Text style={styles.wordText}>{item.en}</Text>
            </View>
            <Ionicons name="volume-high" size={24} color="#888" style={styles.soundIcon} /> {/* Ses ikonu */}
          </View>

          <View style={styles.wordCard}>
            <View style={styles.wordMains}>
                <Text style={styles.wordMain}>Adverbs : </Text>
                {item.means.filter(x=>x.type=='zf.').map((m, index) => (
                    <Text style={styles.wordMain} key={index}>{m.mean}{index < item.means.filter(x=>x.type=='zf.').length - 1 ? ',' : ''}</Text>
                ))}

            </View>
            </View>
            <View style={styles.wordCard}>
            <View style={styles.wordMains}>
                <Text style={styles.wordMain}>Noun : </Text>
                {item.means.filter(x=>x.type=='i.').map((m, index) => (
                    <Text style={styles.wordMain} key={index}>{m.mean}{index < item.means.filter(x=>x.type=='i.').length - 1 ? ',' : ''}</Text>
                ))}

            </View>
            <View style={styles.wordCard}>
            <View style={styles.wordMains}>

                <Text style={styles.wordMain}>Adjective : </Text>
                {item.means.filter(x=>x.type=='s.').map((m, index) => (
                    <Text style={styles.wordMain} key={index}>{m.mean}{index < item.means.filter(x=>x.type=='s.').length - 1 ? ',' : ''}</Text>
                ))}
            </View>
            </View>
            <View style={styles.wordCard}>
             <View style={styles.wordMains}>

                <Text style={styles.wordMain}>Pronoun : </Text>
                {item.means.filter(x=>x.type=='zm.').map((m, index) => (
                    <Text style={styles.wordMain} key={index}>{m.mean}{index < item.means.filter(x=>x.type=='zm.').length - 1 ? ',' : ''}</Text>
                ))}

            </View>
            </View>
            <View style={styles.wordCard}>
            <View style={styles.wordMains}>
                
                <Text style={styles.wordMain}>Verb : </Text>
                {item.means.filter(x=>x.type=='f.').map((m, index) => (
                    <Text style={styles.wordMain} key={index}>{m.mean}{index < item.means.filter(x=>x.type=='f.').length - 1 ? ',' : ''}</Text>
                ))}

            </View>
            </View>

          </View>
          {/* Örnek cümle */}
          <View style={styles.wordCard}>
            <View style={styles.wordDetails}>
            <Text style={styles.exampleSentence}>{/* Örnek cümle */}it is easy</Text>
            </View>
          </View>
          
        </View>
      </View>
    </TouchableOpacity>
  );
  
  const ChatsScreen = ({ onItemLongPress, onItemPress, isMultiSelectMode, selectedItems }) => {
    console.log(selectedItems);
    return (
      <FlatList
        data={chats}
        renderItem={({ item }) => (
          <ChatItem
            item={item}
            onItemLongPress={() => onItemLongPress(item.id)}
            onItemPress={() => onItemPress(item.id)}
            selectedItems={selectedItems}
          />
        )}
        keyExtractor={(item) => item.id}
        style={styles.container}
      />
    );
  };
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#f5f5f5',
    },
  
    listItem: {
      padding: 15,
      borderWidth: 1,
      borderColor: '#e0e0e0',
      borderRadius: 10,
      marginBottom: 10,
    },
    listItemContent: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    avatar: {
      width: '100%',
      height: 100,
      marginRight: 0,
      borderRadius: 10,
    },
    chatInfo: {
      flex: 1,
    },
  
    // Yeni eklenen stiller
    wordCard: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    wordMains: {
        flexDirection: 'row', // Yazıları yatayda sıralamak için
        flexWrap: 'wrap', 
        marginLeft: 10,
    },
    wordMain: {
        marginRight: 5,       // Yazılar arasına boşluk bırakmak için
      },

      wordDetails: {
        flex: 1,
        marginLeft: 10,
      },
    
    wordText: {
      fontSize: 16,
      fontWeight: 'bold',
    },
    soundIcon: {
      marginLeft: 5,
    },
    wordType: {
      fontSize: 14,
      color: '#888',
      
    },
    exampleSentence: {
      marginTop: 10,
      fontSize: 14,
      color: '#555',
    },
  });
  
  export default ChatsScreen;
