const ExcelAddItemForm = ({ onBulkAddItems }) => {
  const handleBulkAddItems = async () => {
    try {
      const result = await DocumentPicker.getDocumentAsync({
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      });

      if (result.type === 'success') {
        // Eğer mobil platformdaysa expo-file-system kullanılabilir
        // Web platformu için uygun bir çözüm bulunmalı
        if (Platform.OS === 'web') {
          // Web ortamında dosya okuma işlemi
          const content = await readFileAsText(result.file);
          console.log('File content:', content);

          // Excel içeriğini JSON formatına çevirme
          const jsonData = convertExcelToJson(content);
          console.log('JSON Data:', jsonData);
        } else {
          // Mobil ortamda expo-file-system kullanma
          const content = await FileSystem.readAsStringAsync(result.uri, {
            encoding: FileSystem.EncodingType.UTF8,
          });
          console.log('File content:', content);

          // Excel içeriğini JSON formatına çevirme
          const jsonData = convertExcelToJson(content);
          console.log('JSON Data:', jsonData);
        }
      }
    } catch (error) {
      console.error('Error picking document', error);
    }
  };

  // Web ortamında dosya okuma işlemi için bir yardımcı fonksiyon
  const readFileAsText = async (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (event) => {
        resolve(event.target.result);
      };
      reader.onerror = (error) => {
        reject(error);
      };
      reader.readAsText(file);
    });
  };

  // Excel içeriğini JSON formatına çevirme fonksiyonu
  const convertExcelToJson = (excelContent) => {
    // Burada excelContent içeriğini işleyerek istediğiniz JSON formatına çevirebilirsiniz.
    // Örneğin, react-native-exceljs gibi bir kütüphane kullanarak Excel içeriğini işleyebilirsiniz.
    // Bu örnek basit bir CSV içeriğini JSON'a çevirmek içindir.

    // CSV içeriğini satırlara ayırma
    const rows = excelContent.split('\n');

    // Başlık (kolon isimleri) satırını al
    const headers = rows[0].split(',');

    // Verileri içeren JSON dizisini oluştur
    const jsonData = rows.slice(1).map((row) => {
      const values = row.split(',');
      const item = {};
      headers.forEach((header, index) => {
        item[header] = values[index];
      });
      return item;
    });

    return jsonData;
  };

  return (
    <View style={styles.formContainer}>
      <TouchableOpacity onPress={handleBulkAddItems} style={styles.addButton}>
        <Text style={styles.buttonText}>Bulk Add Items from Excel</Text>
      </TouchableOpacity>
    </View>
  );
};

