                        <>{m.mean}{index < adverbs.length - 1 ? ',' : ''}</>
                    )}
                    </Text>
                </View>
            </View>:<></>}
            
            {nouns.length!=0?
            <View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Noun : </Text>
                    <Text style={styles.wordMain}>
                    {nouns.map((m, index) => 
                        <>{m.mean}{index < nouns.length - 1 ? ',' : ''}</>
                    )}
                    </Text>
                </View>
            </View>
            :<></>}

            {adjectives.length!=0?
