import React, { useState } from 'react';
import { View, Text, Image, ScrollView, TouchableOpacity, Modal, TextInput, Button } from 'react-native';
import { BarChart } from 'react-native-chart-kit';
import Svg, { Circle, G, Text as SvgText } from 'react-native-svg';
import { PieChart } from 'react-native-svg-charts';

const DailyScreen = ({ user, onItemLongPress }) => {
  const [showModal, setShowModal] = useState(false); // Günlük ekleme modalının görünürlüğünü saklar
  const [dailyLogs, setDailyLogs] = useState([]); // Günlüklerin listesini saklar
  const [newLog, setNewLog] = useState(''); // Yeni eklenecek günlüğü saklar

  // Günlük ekleme işlevi
  const addLog = () => {
    if (newLog.trim() !== '') {
      setDailyLogs([...dailyLogs, newLog]);
      setNewLog(''); // Yeni günlüğü sıfırla
      setShowModal(false); // Modalı kapat
    }
  };

  return (
    <ScrollView style={{ flex: 1 }}>
      {/* Kapak Fotoğrafı ve Profil Resmi */}
      <View>
        {/* Buraya kapak fotoğrafı ve profil resmi gelecek */}
      </View>

      {/* Günlükler Listesi */}
      <View>
        {/* Günlük Ekle Butonu */}
        <TouchableOpacity onPress={() => setShowModal(true)} style={{ alignItems: 'center', backgroundColor: '#1877f2', padding: 10, borderRadius: 5, marginBottom: 20, marginHorizontal: 20 }}>
          <Text style={{ color: 'white' }}>Günlük Ekle</Text>
        </TouchableOpacity>

        {/* Günlükler Listesi */}
        {dailyLogs.map((log, index) => (
          <TouchableOpacity key={index} onPress={() => onItemLongPress(log)}>
            <Text>{log}</Text>
          </TouchableOpacity>
        ))}

        {/* Günlük Ekleme Modalı */}
        <Modal visible={showModal} animationType="slide">
          <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <TextInput
              placeholder="Günlük yazınız..."
              multiline
              value={newLog}
              onChangeText={setNewLog}
              style={{ borderWidth: 1, borderColor: '#ccc', padding: 10, width: '80%', marginBottom: 10 }}
            />
            <Button title="Ekle" onPress={addLog} />
            <Button title="İptal" onPress={() => setShowModal(false)} color="red" />
          </View>
        </Modal>
      </View>
    </ScrollView>
  );
};

export default DailyScreen;
