import React, { useState } from 'react';
import {
  // Other imports...

  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Modal, // Import Modal from react-native-modal
  Button, // Import Button for modal content
} from 'react-native';

const AddGroupFormComponent = () => {
  const [activeForm, setActiveForm] = useState('singleItem');
  const [isModalVisible, setIsModalVisible] = useState(false);

  const handleAddItem = () => {
    setActiveForm('bulkItem');
  };

  const handleBulkAddItems = () => {
    setActiveForm('singleItem');
  };

  return (
    <ScrollView style={{ flexGrow: 1 }}>
      <View style={styles.listItem}>
        <View style={styles.formContainer}>
          <View style={styles.tabMenu}>
            <TouchableOpacity onPress={() => setActiveForm('bulkItem')} style={[styles.tabButton, activeForm === 'bulkItem' && styles.activeTabButton]}>
              <Text style={styles.buttonText}>Kelime Grup Listesi</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setIsModalVisible(true)} style={[styles.tabButton, activeForm === 'singleItem' && styles.activeTabButton]}>
              <Text style={styles.buttonText}>Ekle</Text>
            </TouchableOpacity>
          </View>

          {/* Your existing form content */}
          {activeForm === 'singleItem' && <AddItemForm onAddItem={handleAddItem} />}
          {activeForm === 'bulkItem' && <ExcelAddItemForm onBulkAddItems={handleBulkAddItems} />}

          {/* Modal for the "Ekle" button */}
          <Modal isVisible={isModalVisible} onBackdropPress={() => setIsModalVisible(false)}>
            <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
              <Text>Hello! I am a modal!</Text>
              <Button title="Close" onPress={() => setIsModalVisible(false)} />
            </View>
          </Modal>
        </View>
      </View>
    </ScrollView>
  );
};

// Rest of the component...

export default AddGroupFormComponent;
