import React from 'react';
import { Ionicons } from '@expo/vector-icons';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { Image } from 'react-native';
const chats = [
    { id: '1', name: 'John Doe', message: 'Merhaba, nasılsın?', time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '2', name: 'Jane Smith', message: 'Sonra konuşalım!', time: '14:45', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '3', name: 'Robert Johnson', message: 'Toplantıya katılacak mısın?', time: '13:10', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '4', name: 'John Doe', message: 'Merhaba, nasılsın?', time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '5', name: 'Jane Smith', message: 'Sonra konuşalım!', time: '14:45', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '6', name: 'Robert Johnson', message: 'Toplantıya katılacak mısın?', time: '13:10', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '7', name: 'John Doe', message: 'Merhaba, nasılsın?', time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '8', name: 'Jane Smith', message: 'Sonra konuşalım!', time: '14:45', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '9', name: 'Robert Johnson', message: 'Toplantıya katılacak mısın?', time: '13:10', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '10', name: 'John Doe', message: 'Merhaba, nasılsın?', time: '15:30', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '11', name: 'Jane Smith', message: 'Sonra konuşalım!', time: '14:45', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
    { id: '12', name: 'Robert Johnson', message: 'Toplantıya katılacak mısın?', time: '13:10', avatar: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg' },
   // ... Diğer sohbetler
];
const ChatItem = ({ item, onItemLongPress, onItemPress, selectedItems }) => (
    <TouchableOpacity
      style={[
        styles.listItem,
        {
          marginVertical: 5,
          marginHorizontal: 10,
          backgroundColor: selectedItems.includes(item.id) ? '#ddd' : 'transparent',
        },
      ]}
      onLongPress={() => onItemLongPress(item.id)}
      onPress={() => onItemPress(item.id)}
    >
      <View style={styles.listItemContent}>
        <Image source={{ uri: item.avatar }} style={styles.avatar} />
  
        {/* İngilizce kelime kartı */}
        <View style={styles.wordCard}>
          <View style={styles.wordDetails}>
            <Text style={styles.wordText}>{/* İngilizce kelime */}Easy</Text>
            <Text style={styles.wordType}>{/* Türkçe karşılık */}Kolay</Text>
          </View>
          <Ionicons name="volume-high" size={24} color="#888" style={styles.soundIcon} /> {/* Ses ikonu */}
        </View>
  
        {/* Örnek cümle */}
        <Text style={styles.exampleSentence}>{/* Örnek cümle */}it is easy</Text>
      </View>
    </TouchableOpacity>
  );
  
  const ChatsScreen = ({ onItemLongPress, onItemPress, isMultiSelectMode, selectedItems }) => {
    console.log(selectedItems);
    return (
      <FlatList
        data={chats}
        renderItem={({ item }) => (
          <ChatItem
            item={item}
            onItemLongPress={() => onItemLongPress(item.id)}
            onItemPress={() => onItemPress(item.id)}
            selectedItems={selectedItems}
          />
        )}
        keyExtractor={(item) => item.id}
        style={styles.container}
      />
    );
  };
  
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#f5f5f5',
    },
  
    listItem: {
      padding: 15,
      borderWidth: 1,
      borderColor: '#e0e0e0',
      borderRadius: 10,
      marginBottom: 10,
    },
    listItemContent: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    avatar: {
      width: 50,
      height: 50,
      borderRadius: 10,
      marginRight: 10,
    },
  
    // Yeni eklenen stiller
    wordCard: {
      flexDirection: 'column', // Dikey düzende
      justifyContent: 'space-between',
      alignItems: 'center',
      marginLeft: 10,
    },
    wordDetails: {
      flex: 1,
    },
    wordText: {
      fontSize: 16,
      fontWeight: 'bold',
    },
    soundIcon: {
      marginTop: 5, // Ses ikonunu biraz aşağıda görüntülemek için
    },
    wordType: {
      fontSize: 14,
      color: '#888',
    },
    exampleSentence: {
      marginTop: 10,
      fontSize: 14,
      color: '#555',
    },
  });
  export default ChatsScreen;
