import React, { useState } from 'react';
import { Modal, TouchableOpacity, View, Text, StyleSheet } from 'react-native';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import { Ionicons } from '@expo/vector-icons';



const styles = StyleSheet.create({
  // ... (diğer stiller)
  buttonContent: {
    flexDirection: 'row',
    alignItems: 'center',
    zIndex: 8,
  },
  addItemButton: {
    padding: 10,
    borderWidth: 1,
    borderColor: '#e0e0e0',
    borderRadius: 10,
    marginBottom: 10,
    marginTop: 10,
    alignItems: 'center',
    zIndex: 8,
  },
  addItemText: {
    fontSize: 15,
    fontWeight: 'bold',
    zIndex: 8,
  },
  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
    zIndex: 8,
  },
  modalTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    zIndex: 8,
  },
  addButton: {
    backgroundColor: '#4CAF50',
    padding: 10,
    borderRadius: 5,
    marginVertical: 10,
    zIndex: 8,
  },
  addButtonText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
    textAlign: 'center',
    zIndex: 8,
  },
  closeButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    zIndex: 1,
  },
    dropdownContainer: {
    position: 'absolute',
    top: 70, // Butonun altında çıkması için gerekli değeri ayarlayabilirsiniz.
    right: 10,
    backgroundColor: 'white',
    borderRadius: 5,
    elevation: 5,
    padding: 5,
    zIndex: 10,
  },
  dropdownItem: {
    padding: 10,
  },
});
 

export default WorkItem;
