import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import { View, Text, Image, TouchableOpacity, StyleSheet, Alert, Modal } from 'react-native';

import ChatsScreen from './ChatsScreen';
import { createStackNavigator } from '@react-navigation/stack';
const SohbetScreen = () => {
    return <View><ChatsScreen/></View>;
}

const DurumScreen = () => {
    return <View><Text>Status Screen</Text></View>;
}

const AramalarScreen = () => {
    return <View><Text>Calls Screen</Text></View>;
}

const Tab = createBottomTabNavigator();
const ChatsStack = createStackNavigator();
const ChatsStackNavigator = () => {
    const [modalVisible, setModalVisible] = useState(false);

    return (
        <ChatsStack.Navigator>
            <ChatsStack.Screen 
                name="Sohbetler" 
                component={SohbetScreen}
                options={({ navigation }) => ({
                    headerTitle: 'Sohbetler',
                    headerTintColor: '#fff',
                    headerStyle: {
                        backgroundColor: '#075e54'
                    },
                    headerRight: () => (
                        <View style={{ flexDirection: 'row', marginRight: 10 }}>
                            <TouchableOpacity 
                                style={{ marginRight: 20 }}
                                onPress={() => {
                                    // Arama işlevselliği buraya...
                                }}
                            >
                                <Ionicons name="search" size={24} color="#fff" />
                            </TouchableOpacity>

                            <TouchableOpacity 
                                onPress={() => {
                                    setModalVisible(true);
                                    DropdownMenu(modalVisible);
                                }}
                            >
                                <Ionicons name="ellipsis-vertical" size={24} color="#fff" />
                            </TouchableOpacity>
                        </View>
                    ),
                })}
            />
        </ChatsStack.Navigator>
    );
};

const DropdownMenu = ({ isVisible, onClose }) => {
    return (
        <Modal
            animationType="slide"
            transparent={true}
            visible={isVisible}
            onRequestClose={onClose}
        >
            <View style={styles.modalView}>
                <TouchableOpacity onPress={onClose} style={styles.menuItem}>
                    <Text>Yeni Grup</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={onClose} style={styles.menuItem}>
                    <Text>Yeni Topluluk</Text>
                </TouchableOpacity>
            </View>
        </Modal>
    );
};

const WhatsAppHeader2 = () => {
  return (
    <NavigationContainer>
      <Tab.Navigator
        initialRouteName="Chats"
        tabBarOptions={{
          activeTintColor: '#25D366',
          style: {
            backgroundColor: '#075e54'
          },
          labelStyle: {
            fontSize: 12,
          },
        }}
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;

            if (route.name === 'Sohbetler') {
              iconName = focused ? 'chatbubbles' : 'chatbubbles-outline';
            } else if (route.name === 'Durum') {
              iconName = focused ? 'ellipse' : 'ellipse-outline';
            } else if (route.name === 'Aramalar') {
              iconName = focused ? 'call' : 'call-outline';
            }

            return <Ionicons name={iconName} size={size} color={color} />;
          }
        })}
      >
         <Tab.Screen 
            name="Sohbetler" 
            component={ChatsStackNavigator} 
            options={{ headerShown: false }} // Bu satırı ekledik.
        />
        <Tab.Screen name="Durum" component={DurumScreen} />
        <Tab.Screen name="Aramalar" component={AramalarScreen} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}
const styles = StyleSheet.create({
    modalView: {
        marginTop: 22,
        marginRight: 10,
        alignSelf: 'flex-end',
        backgroundColor: "white",
        borderRadius: 10,
        padding: 15,
        elevation: 5
    },
    menuItem: {
        padding: 10,
    }
});




export default WhatsAppHeader2;
