import React from 'react';
import { Ionicons } from '@expo/vector-icons';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { Image } from 'react-native';
import FontAwesome from 'react-native-vector-icons/FontAwesome'; // FontAwesome eklenen kütüphanenin ismi

const chats = [
    {
        id: '1', en: 'about', means: [
            { mean: 'hemen hemen', type: 'zf.' },
            { mean: 'aşağı yukarı', type: 'zf.' },
            { mean: 'yaklaşık', type: 'zf.' },
        ],
        examples: [{ name: 'it is about' }, { name: 'she is about' }]
        , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg'
    },
    {
        id: '2', en: 'above', means: [
            { mean: 'üzerine', type: 'zf.' },
            { mean: 'yukarısında', type: 'zf.' },
            { mean: 'yukarıda', type: 'zf.' },
        ],
        examples: [{ name: 'it is above' }, { name: 'she is above' }]
        , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg'
    },

    {
        id: '1', en: 'action', means: [
            { mean: 'çalışma', type: 'i.' },
            { mean: 'davranış', type: 'i.' },
            { mean: 'aksiyon', type: 'i.' },
        ],
        examples: [{ name: 'it is action' }, { name: 'she is action' }]
        , image: 'https://images-na.ssl-images-amazon.com/images/S/pv-target-images/ae4816cade1a5b7f29787d0b89610132c72c7747041481c6619b9cc3302c0101._RI_TTW_.jpg'
    },
    {
        id: '2', en: 'above', means: [
            { mean: 'üzerine', type: 'zf.' },
            { mean: 'yukarısında', type: 'zf.' },
            { mean: 'yukarıda', type: 'zf.' },
        ],
        examples: [{ name: 'it is above' }, { name: 'she is above' }]
        , image: ''
    },


    // ... Diğer sohbetler
];

const ChatItem = ({ item, onItemLongPress, onItemPress, selectedItems }) => {
    const adverbs = item.means.filter(x => x.type === 'zf.');
    const nouns = item.means.filter(x => x.type === 'i.');
    const adjectives = item.means.filter(x => x.type === 's.');
    const pronouns = item.means.filter(x => x.type === 'zm.');
    const verbs = item.means.filter(x => x.type === 'f.');

    // ...

    return (
        <TouchableOpacity
        style={[
            styles.listItem,
            {
                marginVertical: 5,
                marginHorizontal: 10,
                backgroundColor: selectedItems.includes(item.id) ? '#ddd' : 'transparent',
            },
        ]}
        onLongPress={() => onItemLongPress(item.id)}
        onPress={() => onItemPress(item.id)}
    >
            {item.image!=''?<Image source={{ uri: item.image }} style={styles.avatar} />:<></>}

            <View style={styles.listItemContent}>

            <View style={styles.chatInfo}>
                {/* İngilizce kelime kartı */}
                <View style={styles.wordCard}>
                    <View style={styles.wordDetails}>
                        <Text style={styles.wordText}>{item.en}</Text>
                    </View>
                    <Ionicons name="volume-high" size={24} color="#888" style={styles.soundIcon} /> {/* Ses ikonu */}
                </View>
            {adverbs.length!=0?<View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Adverbs : </Text>
                    {adverbs.map((m, index) => 
                        <Text style={styles.wordMain} key={index}>{m.mean}{index < adverbs.length - 1 ? ',' : ''}</Text>
                    )}
                </View>
            </View>:<></>}
            
            {nouns.length!=0?
            <View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Noun : </Text>
                    {nouns.map((m, index) => 
                        <Text style={styles.wordMain} key={index}>{m.mean}{index < nouns.length - 1 ? ',' : ''}</Text>
                    )}
                </View>
            </View>
            :<></>}

            {adjectives.length!=0?
            <View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Adjective : </Text>
                    {adjectives.map((m, index) => 
                        <Text style={styles.wordMain} key={index}>{m.mean}{index < adjectives.length - 1 ? ',' : ''}</Text>
                    )}
                </View>
            </View>
             :<></>}


            {pronouns.length!=0?
            <View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Pronoun : </Text>
                    {pronouns.map((m, index) => 
                        <Text style={styles.wordMain} key={index}>{m.mean}{index < pronouns.length - 1 ? ',' : ''}</Text>
                    )}
                </View>
            </View>
            :<></>}

            {verbs.length!=0?
            <View style={styles.wordCard}>
                <View style={styles.wordMains}>
                    <Text style={styles.wordMain}>Verb : </Text>
                    {verbs.map((m, index) => 
                        <Text style={styles.wordMain} key={index}>{m.mean}{index < verbs.length - 1 ? ',' : ''}</Text>
                    )}
                </View>
            </View>
            :<></>}

            {item.examples.length!=0?
           <View style={[styles.wordCard, { backgroundColor: '#b4e0ff', borderRadius: 5, marginTop: 4, textAlign: "center" }]}>
                <View >
                    <Text style={{ textAlign: "left",marginTop:4,marginBottom:4,marginLeft:10 }}>Examples :</Text>
                    {item.examples.map((m, index) => (
                        <View style={[styles.wordMains,{marginTop:4,marginBottom:4,marginLeft:10}]} key={index}>
                            <FontAwesome name="circle" size={10} color="#000" style={{ marginRight: 5 }} /> {/* Nokta ikonu */}
                            <Text style={[styles.exampleSentence,{lineHeight:'2px'}]}>{m.name}</Text>
                        </View>
                    ))}
                </View>
            </View>
            :<></>}
            </View>
            </View>
        </TouchableOpacity>
    );
};




const handleAddItem = () => {
    // Burada yeni bir öğe eklemek için gerekli işlemleri gerçekleştirebilirsiniz.
    // Örneğin, bir form gösterip kullanıcının girdiği bilgilerle yeni bir öğe oluşturabilirsiniz.

    // Örnek:
    const newItem = {
        id: '3',
        en: 'newWord',
        means: [
            { mean: 'yeni', type: 'zf.' },
            { mean: 'eklenen', type: 'zf.' },
            { mean: 'kelime', type: 'zf.' },
        ],
        examples: [{ name: 'it is new' }, { name: 'she is added' }],
        image: 'https://example.com/newImage.jpg',
    };

    onAddItem(newItem);

    // İşlem tamamlandığında modal'ı kapat
    setModalVisible(false);
};

const ChatsScreen = ({ onItemLongPress, onItemPress, isMultiSelectMode, selectedItems }) => {
    console.log(selectedItems);

    
    
    return (
       <>
           <TouchableOpacity
            style={styles.addItemButton}
            onPress={() => setModalVisible(true)}
        >
            <Modal visible={isModalVisible} animationType="slide">
                <View style={styles.modalContainer}>
                    {/* Yeni öğe eklemek için bir form veya bilgi girişi alanları ekleyebilirsiniz. */}
                    <Text style={styles.modalTitle}>Yeni Kelime Ekle</Text>
                    {/* ... (yeni öğe ekleme formu) */}
                    <TouchableOpacity style={styles.addButton} onPress={handleAddItem}>
                        <Text style={styles.addButtonText}>Ekle</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.closeButton} onPress={() => setModalVisible(false)}>
                        <FontAwesome name="close" size={24} color="#000" />
                    </TouchableOpacity>
                </View>
            </Modal>
            <Text style={styles.addItemText}>Yeni Kelime Ekle</Text>
        </TouchableOpacity>
        <FlatList
            data={chats}
            renderItem={({ item }) => (
                <ChatItem
                    item={item}
                    onItemLongPress={() => onItemLongPress(item.id)}
                    onItemPress={() => onItemPress(item.id)}
                    selectedItems={selectedItems}
                />
            )}
            keyExtractor={(item) => item.id}
            style={styles.container}
        /></>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },

    listItem: {
        padding: 15,
        borderWidth: 1,
        borderColor: '#e0e0e0',
        borderRadius: 10,
        marginBottom: 10,
    },
    listItemContent: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    avatar: {
        width: '100%',
        height: 100,
        marginRight: 0,
        borderRadius: 10,
    },
    chatInfo: {
        flex: 1,
    },

    // Yeni eklenen stiller
    wordCard: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    wordMains: {
        flexDirection: 'row', // Yazıları yatayda sıralamak için
        flexWrap: 'wrap',
        marginLeft: 10,
    },
    wordMain: {
        marginRight: 5,       // Yazılar arasına boşluk bırakmak için
    },

    wordDetails: {
        flex: 1,
        marginLeft: 10,
    },

    wordText: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    soundIcon: {
        marginLeft: 5,
    },
    wordType: {
        fontSize: 14,
        color: '#888',

    },
    exampleSentence: {
        marginTop: 4,
        fontSize: 14,
        color: '#555',
    },
});

export default ChatsScreen;
