import React from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import { Image } from 'react-native';
const chats = [
    { id: '1', name: 'John Doe', message: 'Merhaba, nasılsın?', avatar: 'https://example.com/avatar1.jpg' },
    { id: '2', name: 'Jane Smith', message: 'Sonra konuşalım!', avatar: 'https://example.com/avatar2.jpg' },
    { id: '3', name: 'Robert Johnson', message: 'Toplantıya katılacak mısın?', avatar: 'https://example.com/avatar3.jpg' },
    // ... Diğer sohbetler
];

const ChatItem = ({ item }) => (
    <TouchableOpacity style={styles.listItem}>
    <Image source={{ uri: item.avatar }} style={styles.avatar} />
    <View style={styles.chatInfo}>
        <Text style={styles.listItemText}>{item.name}</Text>
        <Text style={styles.listItemMessage}>{item.message}</Text>
    </View>
</TouchableOpacity>
);

const ChatsScreen = () => {
    return (
        <FlatList
            data={chats}
            renderItem={({ item }) => <ChatItem item={item} />}
            keyExtractor={item => item.id}
            style={styles.container}
        />
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5'
    },
    listItem: {
        flexDirection: 'row',
        padding: 15,
        borderBottomWidth: 1,
        borderBottomColor: '#e0e0e0'
    },
    listItemText: {
        fontSize: 18,
        fontWeight: 'bold'
    },
    listItemMessage: {
        fontSize: 14,
        color: '#888'
    },
    avatar: {
        width: 50,
        height: 50,
        borderRadius: 25,
        marginRight: 10
    },
    chatInfo: {
        flex: 1
    }
});


export default ChatsScreen;
