import React from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';
import { Ionicons } from '@expo/vector-icons';  // Bu ikonları kullanabilmek için öncelikle `expo install @expo/vector-icons` komutunu çalıştırmanız gerekmektedir.

const WhatsAppHeader = ({ title }) => {
    return (
        <View style={styles.headerContainer}>
            <Text style={styles.headerTitle}>{title}</Text>
            <View style={styles.iconsContainer}>
                <TouchableOpacity>
                    <Ionicons name="search" size={24} color="white" style={styles.icon} />
                </TouchableOpacity>
                <TouchableOpacity>
                    <Ionicons name="ellipsis-vertical" size={24} color="white" style={styles.icon} />
                </TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    headerContainer: {
        flexDirection: 'row',
        // justifyContent: 'space-between',
        alignItems: 'center',
        padding: 10,
        backgroundColor: '#075e54'
    },
    headerTitle: {
        color: 'white',
        fontSize: 20,
        fontWeight: 'bold'
    },
    iconsContainer: {
        flexDirection: 'row'
    },
    icon: {
        marginLeft: 10
    }
});

export default WhatsAppHeader;
