import React from 'react';
import { View, Text, StyleSheet, ImageBackground, TouchableOpacity } from 'react-native';

const ProfileScreen = ({ user }) => {
    return (
        <View style={styles.container}>
            <ImageBackground
                source={{ uri: user.backgroundImage }}
                style={styles.backgroundImage}
                resizeMode="cover"
            >
                <View style={styles.profileContainer}>
                    <View style={styles.avatarContainer}>
                        <ImageBackground
                            source={{ uri: user.avatar }}
                            style={styles.avatar}
                            imageStyle={{ borderRadius: 75 }}
                        />
                    </View>
                    <View style={styles.userInfo}>
                        <Text style={styles.username}>{user.username}</Text>
                        <Text style={styles.bio}>{user.bio}</Text>
                        <TouchableOpacity style={styles.button}>
                            <Text style={styles.buttonText}>Edit Profile</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </ImageBackground>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    backgroundImage: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    profileContainer: {
        backgroundColor: 'rgba(255, 255, 255, 0.7)',
        padding: 20,
        borderRadius: 10,
        flexDirection: 'row', // Profil resmi ve bilgileri yan yana sırala
        alignItems: 'center', // Dikey hizalama
    },
    avatarContainer: {
        borderWidth: 5,
        borderRadius: 80, // Yarıçapı, yarısı kadar olacak şekilde
        borderColor: '#fff',
    },
    avatar: {
        width: 160,
        height: 160,
    },
    userInfo: {
        flex: 1, // Bilgilerin sağa yaslanmasını sağlar
        marginLeft: 20, // Profil resmi ile bilgiler arasındaki boşluğu ayarlar
    },
    username: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 5,
        color: '#333', // Kullanıcı adının rengini ayarlar
    },
    bio: {
        fontSize: 16,
        marginBottom: 20,
        color: '#666', // Biyografinin rengini ayarlar
    },
    button: {
        backgroundColor: '#007bff',
        paddingHorizontal: 20,
        paddingVertical: 10,
        borderRadius: 5,
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
    },
});

export default ProfileScreen;
