import React, { useState } from 'react';
import { View, Text, TextInput, Button, Image, TouchableOpacity, StyleSheet } from 'react-native';

const AddItemForm = ({ onAddItem }) => {
  const [formData, setFormData] = useState({
    en: '',
    zf: '',
    image: '',
    example1: '',
    example2: '',
  });

  const handleInputChange = (name, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleAddItem = () => {
    // Form verilerini kullanarak yeni bir öğe oluşturun
    const newItem = {
      id: generateUniqueId(), // Bu, benzersiz bir kimlik üretecek bir işlev olmalı
      en: formData.en,
      means: [{ mean: formData.zf, type: 'zf.' }], // Örneğin sadece bir anlam ekliyorum, siz isteğinize göre ayarlayabilirsiniz
      examples: [
        { name: formData.example1 },
        { name: formData.example2 },
      ],
      image: formData.image,
    };

    // onAddItem işlevini çağırarak yeni öğeyi iletilen işlevi çağırın
    onAddItem(newItem);

    // Form verilerini sıfırlayın
    setFormData({
      en: '',
      zf: '',
      image: '',
      example1: '',
      example2: '',
    });
  };

  return (
    <View style={styles.formContainer}>
      <Text style={styles.label}>English Word:</Text>
      <TextInput
        style={styles.input}
        value={formData.en}
        onChangeText={(text) => handleInputChange('en', text)}
      />

      <Text style={styles.label}>Meaning:</Text>
      <TextInput
        style={styles.input}
        value={formData.zf}
        onChangeText={(text) => handleInputChange('zf', text)}
      />

      <Text style={styles.label}>Image URL:</Text>
      <TextInput
        style={styles.input}
        value={formData.image}
        onChangeText={(text) => handleInputChange('image', text)}
      />

      <Text style={styles.label}>Example 1:</Text>
      <TextInput
        style={styles.input}
        value={formData.example1}
        onChangeText={(text) => handleInputChange('example1', text)}
      />

      <Text style={styles.label}>Example 2:</Text>
      <TextInput
        style={styles.input}
        value={formData.example2}
        onChangeText={(text) => handleInputChange('example2', text)}
      />

      <TouchableOpacity onPress={handleAddItem} style={styles.addButton}>
        <Text style={styles.buttonText}>Add Item</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  formContainer: {
    padding: 10,
    backgroundColor: '#fff',
    borderRadius: 10,
    marginBottom: 10,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingLeft: 10,
  },
  addButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
    alignItems: 'center',
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
  },
});

export default AddItemForm;
