import React, { useState } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import { View, Text, Image, TouchableOpacity, StyleSheet, Alert, Modal } from 'react-native';
import logo from '../assets/icon.png';
import ChatsScreen from './ChatsScreen';
import { createStackNavigator } from '@react-navigation/stack';


const SohbetScreen = () => {
    const [isMultiSelectMode, setIsMultiSelectMode] = useState(false);
    const [selectedItems, setSelectedItems] = useState([]);

    
const handleSelectItem = (itemId) => {
    if (selectedItems.includes(itemId)) {
        setSelectedItems(prevItems => prevItems.filter(id => id !== itemId));
    } else {
        setSelectedItems(prevItems => [...prevItems, itemId]);
    }
};

const handleItemLongPress = (itemId) => {
    console.log(itemId);
    setIsMultiSelectMode(true);
    handleSelectItem(itemId);
};

const handleItemPress = (itemId) => {
    if (isMultiSelectMode) {
        handleSelectItem(itemId);
    }
};

const handleDeleteSelectedItems = () => {
    // selectedItems dizisini kullanarak Ã¶Äeleri silin
    // Daha sonra Ã§oklu seÃ§im modunu kapatalÄ±m ve seÃ§ili Ã¶Äeleri sÄ±fÄ±rlayalÄ±m
    setIsMultiSelectMode(false);
    setSelectedItems([]);
};

    return <View><ChatsScreen   onItemLongPress={handleItemLongPress}
    onItemPress={handleItemPress}
    isMultiSelectMode={isMultiSelectMode}
    selectedItems={selectedItems}
    /></View>;
}

const DurumScreen = () => {
    return <View><Text>Status Screen</Text></View>;
}

const AramalarScreen = () => {
    return <View><Text>Calls Screen</Text></View>;
}

const Tab = createBottomTabNavigator();
const ChatsStack = createStackNavigator();




const WhatsAppHeader2 = () => {

    const [modalVisible, setModalVisible] = useState(false);
    const ChatsStackNavigator = () => {
    
    
        return (
            <ChatsStack.Navigator>
                <ChatsStack.Screen 
                    name="Sohbetler" 
                    component={SohbetScreen}
                    options={({ navigation }) => ({
                        headerTitle: () => (
                            <View><Text style={styles.logo}>TALKLAN</Text></View>
                          ),
                        headerTintColor: '#fff',
                        headerStyle: {
                            backgroundColor: '#0077B6'
                        },
                        headerRight: () => (
                            <>
                            <View style={{ flexDirection: 'row', marginRight: 10 }}>
                                <TouchableOpacity 
                                    style={{ marginRight: 20 }}
                                    onPress={() => {
                                        // Arama iÅlevselliÄi buraya...
                                    }}
                                >
                                    <Ionicons name="search" size={24} color="#fff" />
                                </TouchableOpacity>
    
                                <TouchableOpacity 
                                    onPress={() => {
                                        setModalVisible(true);
                                    }}
                                >
                                    <Ionicons name="ellipsis-vertical" size={24} color="#fff" />
                                </TouchableOpacity>
                            </View>
                              <DropdownMenu 
                              isVisible={modalVisible} 
                              onClose={() => setModalVisible(false)} 
                          />
                          </>
                        ),
                    })}
                />
               
            </ChatsStack.Navigator>
        );
    };
    
    const DropdownMenu = ({ isVisible, onClose }) => {
        if (!isVisible) return null;
      
        return (
          <View style={styles.dropdown}>
            <TouchableOpacity onPress={() => { /* Yeni grup iÅlemleri... */ onClose(); }}>
              <Text style={styles.dropdownItem}>Yeni Grup</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => { /* Yeni topluluk iÅlemleri... */ onClose(); }}>
              <Text style={styles.dropdownItem}>Yeni Topluluk</Text>
            </TouchableOpacity>
          </View>
        );
      };

      

  return (
    <NavigationContainer>
      <Tab.Navigator
        initialRouteName="Chats"
        tabBarOptions={{
          activeTintColor: '#0077B6',
          style: {
            backgroundColor: '#0077B6'
          },
          labelStyle: {
            fontSize: 12,
          },
        }}
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;

            if (route.name === 'Sohbetler') {
              iconName = focused ? 'chatbubbles' : 'chatbubbles-outline';
            } else if (route.name === 'Durum') {
              iconName = focused ? 'ellipse' : 'ellipse-outline';
            } else if (route.name === 'Aramalar') {
              iconName = focused ? 'call' : 'call-outline';
            }

            return <Ionicons name={iconName} size={size} color={color} />;
          }
        })}
      >
         <Tab.Screen 
            name="Sohbetler" 
            component={ChatsStackNavigator} 
            options={{ headerShown: false }} // Bu satÄ±rÄ± ekledik.
        />
        <Tab.Screen name="Durum" component={DurumScreen} />
        <Tab.Screen name="Aramalar" component={AramalarScreen} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}
const styles = StyleSheet.create({
    dropdown: {
      position: 'absolute',
      top: 40, // header'Ä±n yÃ¼ksekliÄini geÃ§mesi iÃ§in bu deÄeri ayarlayabilirsiniz.
      right: 10,
      backgroundColor: 'white',
      borderRadius: 5,
      elevation: 5,
      padding: 5,
    },
    dropdownItem: {
      padding: 10,
    },
    logo: {
        fontSize: 20,
        color:"#fff",
        fontWeight:"bold"
      }
  });



export default WhatsAppHeader2;
