import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import WordBook from './component/WordBook';
import LoginScreen from './component/LoginScreen';
import { NavigationContainer } from '@react-navigation/native';

export default function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Burada kullanıcı adı ve şifrenin kontrolü yapılabilir
    if (username === 'demo' && password === 'demo') {
      // Doğru kullanıcı adı ve şifre ile giriş yapılacaksa işlemler burada yapılır
      setIsLoggedIn(true);
    } else {
      setError('Kullanıcı adı veya şifre hatalı.');
    }
  };

  return (
    <NavigationContainer>
      <View style={styles.container}>
        {isLoggedIn ? <WordBook /> : <LoginScreen handleLogin={handleLogin} />}
      </View>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});
